import{_ as s,o as a,c as e,V as o}from"./chunks/framework.b1ba171e.js";const b=JSON.parse('{"title":"使用docker進行部署","description":"","frontmatter":{},"headers":[],"relativePath":"zh/docs/bodhi_setup.md","filePath":"zh/docs/bodhi_setup.md"}'),t={name:"zh/docs/bodhi_setup.md"},n=o(`<h1 id="使用docker進行部署" tabindex="-1">使用docker進行部署 <a class="header-anchor" href="#使用docker進行部署" aria-label="Permalink to &quot;使用docker進行部署&quot;">​</a></h1><p>你的環境需要安裝 <code>git, nano, docker(with compose plugin)</code></p><p><strong>dc 等同於 docker compose</strong></p><h2 id="準備" tabindex="-1">準備 <a class="header-anchor" href="#準備" aria-label="Permalink to &quot;準備&quot;">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">git clone https://github.com/lotusnetwork/bodhi-docker.git</span></span></code></pre></div><p><strong>如若想於arm的伺服器上使用bodhi,請將docker-compose.yml中的</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">image: ghcr.io/lotusnetwork/bodhi-docker</span></span></code></pre></div><p><strong>替換爲</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">build: https://github.com/lotusnetwork/bodhi-docker.git</span></span></code></pre></div><h2 id="準備您的tls證書" tabindex="-1">準備您的tls證書 <a class="header-anchor" href="#準備您的tls證書" aria-label="Permalink to &quot;準備您的tls證書&quot;">​</a></h2><p>拷貝您的證書與私鑰至bodhi-docker/data</p><p>現在您的目錄格式應該如下</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">|- docker-compose.yml</span></span>
<span class="line"><span style="color:#A6ACCD;">|- data/</span></span>
<span class="line"><span style="color:#A6ACCD;"> |- hysteria</span></span>
<span class="line"><span style="color:#A6ACCD;"> |- config.ini</span></span>
<span class="line"><span style="color:#A6ACCD;"> |- cert.crt</span></span>
<span class="line"><span style="color:#A6ACCD;"> |- key.key</span></span>
<span class="line"><span style="color:#A6ACCD;"> |- bin/</span></span>
<span class="line"><span style="color:#A6ACCD;">  |- yq</span></span></code></pre></div><p><strong>data文件夾將會被映射到容器內的/gateway</strong></p><h2 id="修改配置檔" tabindex="-1">修改配置檔 <a class="header-anchor" href="#修改配置檔" aria-label="Permalink to &quot;修改配置檔&quot;">​</a></h2><p><code>upstream_api=https://example.com &lt;- board address</code> <strong>修改它</strong></p><p><code>api_port=7653 &lt;- Hysteria api port</code></p><p><code>nodeid=1 &lt;- remote nodeID</code> <strong>修改它</strong></p><p><code>core_path=/gateway/hysteria 到hysteria核心的路徑(容器內)</code></p><p><code>tls_cert=/gateway/cert.crt &lt;-path to tls cert</code> <strong>修改它</strong></p><p><code>tls_key=/gateway/key.key &lt;- path to tls key</code> <strong>修改它</strong></p><p><code>psk=leuleuleuleu &lt;- password for nodeAPI</code> <strong>修改它</strong></p><p><code>obfs=true/false &lt;- 啓用/禁用 obfs 於 hysteria 中</code> <strong>如果您不使用lotusboard請將其設定爲true</strong></p><p><code>acl=</code> <strong>填入你的acl配置檔完整路徑</strong></p><h2 id="啓動-bodhi" tabindex="-1">啓動 bodhi <a class="header-anchor" href="#啓動-bodhi" aria-label="Permalink to &quot;啓動 bodhi&quot;">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">dc up -d</span></span></code></pre></div>`,26),l=[n];function p(c,r,d,i,h,g){return a(),e("div",null,l)}const y=s(t,[["render",p]]);export{b as __pageData,y as default};
