import{_ as t,o as e,c as a,V as l}from"./chunks/framework.b1ba171e.js";const b=JSON.parse('{"title":"Lotusboard功能开关","description":"","frontmatter":{},"headers":[],"relativePath":"zhs/docs/lotusboard_tag.md","filePath":"zhs/docs/lotusboard_tag.md"}'),o={name:"zhs/docs/lotusboard_tag.md"},s=l('<h1 id="lotusboard功能开关" tabindex="-1">Lotusboard功能开关 <a class="header-anchor" href="#lotusboard功能开关" aria-label="Permalink to &quot;Lotusboard功能开关&quot;">​</a></h1><blockquote><p>在Lotusboard中,我们使用tag来为某个节点启用/停用某功能</p></blockquote><p>当你想要为某个节点启用一个功能时, 为给节点加上一个特殊的标签</p><h2 id="可用的开关" tabindex="-1">可用的开关 <a class="header-anchor" href="#可用的开关" aria-label="Permalink to &quot;可用的开关&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">TAG</th><th style="text-align:center;">Feature</th><th style="text-align:right;">TYPE</th></tr></thead><tbody><tr><td style="text-align:left;">VLESS</td><td style="text-align:center;">面板端启用VLESS分发</td><td style="text-align:right;">VMESS</td></tr><tr><td style="text-align:left;">XTLS</td><td style="text-align:center;">面板端启用VLESS flow分发</td><td style="text-align:right;">VMESS</td></tr><tr><td style="text-align:left;">hy2</td><td style="text-align:center;">面板端启用hysteria2分发</td><td style="text-align:right;">Hysteria</td></tr></tbody></table><h2 id="添加一个vless节点" tabindex="-1">添加一个VLESS节点 <a class="header-anchor" href="#添加一个vless节点" aria-label="Permalink to &quot;添加一个VLESS节点&quot;">​</a></h2><blockquote><p>请先升级到最新版本</p></blockquote><p>在lotusboard docker compose目录下执行 <code>./update.sh</code> 来升级到最新版本</p><blockquote><p>添加VLESS节点</p></blockquote><ol><li>首先在面板中加一个V2ray(VMESS节点)</li><li>然后在 &quot;节点标签&quot; (NodeTags)中,添加一个叫&quot;VLESS&quot;的标签</li><li>修改您的后端配置来在节点上启用VLESS</li></ol><p>例如在 XrayR 中</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">EnableVless: true</span></span></code></pre></div><h2 id="添加一个vless-flow节点" tabindex="-1">添加一个VLESS flow节点 <a class="header-anchor" href="#添加一个vless-flow节点" aria-label="Permalink to &quot;添加一个VLESS flow节点&quot;">​</a></h2><ol><li>首先在面板中加一个V2ray(VMESS节点)</li><li>然后在 &quot;节点标签&quot; (NodeTags)中,添加一个叫&quot;VLESS&quot;的标签,与一个叫&quot;XTLS&quot;的标签</li><li>修改您的后端配置来在节点上启用VLESS</li></ol><p>例如在 XrayR 中</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">EnableVless: true</span></span></code></pre></div><h2 id="添加一个hysteria节点" tabindex="-1">添加一个hysteria节点 <a class="header-anchor" href="#添加一个hysteria节点" aria-label="Permalink to &quot;添加一个hysteria节点&quot;">​</a></h2><ol><li>首先在面板中加一个Hysteria节点</li><li>然后在 &quot;节点标签&quot; (NodeTags)中,添加一个叫&quot;hy2&quot;的标签</li><li>修改您的后端配置来在节点上启用Hysteria</li></ol><p>例如，在 bodhi 中，您需要将 hysteria 核心替换为最新的 hysteria2 核心</p>',19),r=[s];function i(d,n,c,h,u,p){return e(),a("div",null,r)}const y=t(o,[["render",i]]);export{b as __pageData,y as default};
